(function () {
  const sidebarElement = document.getElementById('sidebar');
  const json = document.getElementById('sidebar-data')?.textContent;
  const sidebarEntries = JSON.parse(json);
  console.log(sidebarEntries);
  const tabIcons = {
    maps: {
      name: 'Maps',
      icon: 'map',
    },
    analysis: {
      name: 'Analysis',
      icon: 'chart-pie',
    },
    poi: {
      name: 'POI',
      icon: 'map-marker-alt',
    },
  };
  // create menu tabs
  Object.keys(sidebarEntries).forEach((key) => {
    sidebarElement.insertAdjacentHTML('beforeend', `<input class="sidebar-tab-radio" id="sidebar-trigger-${key}" name="sidebar-tabs-group" type="radio" ${key === 'maps' ? 'checked="checked"' : ''}><label class="sidebar-tab" id="sidebar-tab-${key}" for="sidebar-trigger-${key}"><i class="fas fa-${tabIcons[key].icon}"></i>${tabIcons[key].name}</label>`);
  });

  sidebarElement.insertAdjacentHTML('beforeend', '<div class="sidebar-body"></div>');
  const sidebarBody = document.querySelector('.sidebar-body');

  Object.entries(sidebarEntries).forEach(([key, firstLevelItems]) => {
    sidebarBody.insertAdjacentHTML('beforeend', `<ul class="first-level-menu" id="sidebar-panel-${key}"></ul>`);
    const firstLevelMenu = sidebarBody.querySelector('ul:last-of-type');
    firstLevelItems.forEach((firstLevelItem) => firstLevelMenu.insertAdjacentHTML('beforeend', `<li>${firstLevelItem.label}</li>`));
    sidebarBody.appendChild(firstLevelMenu);
  });
}());

