function renderMenuItems(menu, itemObj, createUl = false, depth = 1){
  let menuItem;

  if(createUl){
    menuItem = document.createElement("ul");
  }
  else{
    menuItem = document.createElement("li");
  }

  menuItem.classList.add("menu-item");
  menuItem.insertAdjacentHTML("beforeend",`<span class="item-label">${itemObj.label}</span>`);
  //firstLevelMenu.insertAdjacentHTML('beforeend', `<li>${firstLevelItem.label}</li>`);
  menu.appendChild(menuItem);

  if(itemObj["children"] && itemObj["children"].length){
    menuItem.insertAdjacentHTML("beforeend",'<i class="fas fa-chevron-up"></i>');
    menuItem.classList.add("has-children");
    itemObj["children"].forEach((subItemObj)=>{
      renderMenuItems(menuItem, subItemObj, true, depth + 1);
    })
  }


}

