/**
 * Prepare a specific theming stylesheet for the sidebar using the default configs
 */
function prepareSidebarStyleSheet(defaultConfigs){
    let str = 
    `
    #sidebar[id] .sidebar-tab[class]{
      background:${defaultConfigs.tabBackground};
      color:${defaultConfigs.tabColor};
    }
    #sidebar #sidebar-trigger-maps:checked~#sidebar-tab-maps, 
    #sidebar #sidebar-trigger-analysis:checked~#sidebar-tab-analysis, 
    #sidebar #sidebar-trigger-poi:checked~#sidebar-tab-poi{
      background:${defaultConfigs.tabSelectedBackground};
      color:${defaultConfigs.tabSelectedColor};
    }
    .simplebar-scrollbar::before {
      background-color: ${defaultConfigs.scrollbarColor} !important;
      opacity: ${defaultConfigs.scrollbarOpacity} !important;
    }
    li.menu-item > .menu-item-content-wrapper > svg.left-icon{
      color: ${defaultConfigs.leftIconColor}
    }

    li.menu-item > .menu-item-content-wrapper > svg.right-icon{
      color: ${defaultConfigs.rightIconColor}
    }
    `

    let sidebarStylesheet = document.createElement("style");
    sidebarStylesheet.id = "sidebar-stylesheet";
    document.head.appendChild(sidebarStylesheet);
    sidebarStylesheet.textContent = str;

  }

/**
 * render <li> items of a given menu
 */
function renderMenuItems(menu, itemsArr, depth){

  if(itemsArr.length){
    itemsArr.forEach((itemObj)=>{

      let menuItem = document.createElement("li");
      menuItem.classList.add("menu-item");

      let menuItemContentWrapper = document.createElement("div");
      menuItemContentWrapper.classList.add("menu-item-content-wrapper");

      menuItemContentWrapper.insertAdjacentHTML("beforeend",`<span class="item-label">${itemObj.label}</span>`);

      //console.log("menu = ", menu);
      //console.log("itemObj = ", itemObj);
      //console.log("menuItem = ", menuItem);
      //console.log("_________________________________");

      menuItem.appendChild(menuItemContentWrapper);

      if(depth > 1){
        if(itemObj["icons"]){
          let leftIcons = itemObj["icons"]["left"];
          let rightIcons = itemObj["icons"]["right"];

          console.log("icons = ", itemObj["icons"]);


          if(leftIcons){
            leftIcons.forEach((leftIconObj)=>{
              let leftIconEl;
              //console.log("leftIconObj = ", leftIconObj);

              if(leftIconObj.tag){
                leftIconEl = document.createElement(leftIconObj.tag);
              }
              else{
                leftIconEl = document.createElement("i");
              }

              if(leftIconObj.classes){
                leftIconObj.classes.forEach((className)=>{
                  leftIconEl.classList.add(className);
                });
              }
              else{
                leftIconEl.classList.add("fas");
                leftIconEl.classList.add("fa-check");
              }
              leftIconEl.classList.add("left-icon");

              if(leftIconObj.color){
                leftIconEl.style.cssText += "color: "+leftIconObj.color;
              }

              if(leftIconObj.id){
                leftIconEl.dataset.id = leftIconObj.id;
              }

              if(leftIconObj.toggledClassesOnClick){
                leftIconEl.dataset.toggledClassesOnClick = JSON.stringify(leftIconObj.toggledClassesOnClick);
              }

              menuItemContentWrapper.insertBefore(leftIconEl,menuItemContentWrapper.firstElementChild);

            });
          }
        }
        else{
          let leftIconEl = document.createElement("i");
          leftIconEl.classList.add("fas");
          leftIconEl.classList.add("fa-check");
          leftIconEl.classList.add("left-icon");
          menuItemContentWrapper.insertBefore(leftIconEl, menuItemContentWrapper.firstElementChild);
        }
      }

      if(itemObj["children"] && itemObj["children"].length){
        menuItemContentWrapper.insertAdjacentHTML("beforeend",'<i class="fas fa-chevron-up"></i>');
        menuItem.classList.add("has-children");

        let subMenu = document.createElement("ol");
        subMenu.classList.add("sub-menu");
        menuItem.appendChild(subMenu);

        renderMenuItems(subMenu, itemObj["children"], depth+1);

      }
      menu.appendChild(menuItem);
    });
  }

}

