#sidebar {
  cursor: pointer;
  font-family: "Open sans";
  width: 356px;
  user-select: none;
  margin-right: 10px;
  display: flex;
  flex-direction: column;
  border:1px solid rgba(0, 0, 0, .12);
}

.sidebar-body {
  width: 100%;
}

.sidebar-body ul {
  margin: 0;
  padding: 0;
}

.sidebar-body li {
  padding: 0;
  list-style-type: none;
}

.sidebar-body li:hover {
  background: transparent;
}

.sidebar-body li:last-of-type {
  border-bottom: none;
}

.sidebar-body>ul {
  display: none;
}



#sidebar .sidebar-tab {
  cursor: pointer;
  padding: 12px 24px;
  background: #4490B0;
  display: inline-block;
  color: #fff;
  border-right: 1px solid rgba(255, 255, 255, .3);
}

#sidebar .sidebar-tab i {
  margin-right: 10px;
}

#sidebar .sidebar-tab:first-of-type {
  border-radius: 4px 0 0 4px;
}

#sidebar .sidebar-tab:last-of-type {
  border-radius: 0 4px 4px 0;
}

#sidebar .sidebar-tab-radio {
  display: none;
}

#sidebar #sidebar-trigger-maps:checked~#sidebar-tab-maps,
#sidebar #sidebar-trigger-analysis:checked~#sidebar-tab-analysis,
#sidebar #sidebar-trigger-poi:checked~#sidebar-tab-poi {
  background: #81B4CA;
}

#sidebar #sidebar-trigger-maps:checked~.sidebar-body #sidebar-panel-maps,
#sidebar #sidebar-trigger-analysis:checked~.sidebar-body #sidebar-panel-analysis,
#sidebar #sidebar-trigger-poi:checked~.sidebar-body #sidebar-panel-poi {
  display: block;
}

#sidebar label:last-of-type {
  text-transform: uppercase;
}
/*******************/
.simplebar-content{
    display: flex;
    flex-wrap: wrap;
}

.sidebar-tab{
  flex-grow:1;
}

.sidebar-tab > svg {
    margin-right: 7px;
}

label > svg{
    pointer-events: none;
}

.first-level-menu{
  width:100%;
}

.first-level-menu > .menu-item {
    font-weight: 600;
    display: flex;
    align-items: center;
    word-break: break-word;
    padding: 0px;
    position: relative;
}

.menu-item-content-wrapper{
  padding: 13px 5px 13px 8px;
  width: 100%;
}

ul.first-level-menu > li > .menu-item-content-wrapper::before {
    content: "";
    width: 14px;
    height: 14px;
    min-width: 14px;
    min-height: 14px;
    margin-right: 15px;
    border: 1px solid gray;
    border-radius: 100%;
}

ul.first-level-menu > li.is-selected >.menu-item-content-wrapper::before {
    box-shadow: inset 0 0 0px 4px #FFFFFF, inset 0 0 0px 10px gray;
}

ul.first-level-menu > li > .menu-item-content-wrapper > .item-label {
    flex-grow: 1;
}


span.item-label {
    flex-grow: 1;
}

.sub-menu {
    width: 100%;
    padding: 0;
    font-weight: 400;
}

li.menu-item > .menu-item-content-wrapper{
    display: flex;
    align-items: center;
    border-bottom:1px solid rgba(0, 0, 0, .12);
}

li.menu-item.has-children > .menu-item-content-wrapper{
    display: flex;
    align-items: center;
}

li.menu-item.has-children > .menu-item-content-wrapper > .fa-chevron-up{
  pointer-events: none;
}

li.menu-item.has-children > .menu-item-content-wrapper > .fa-chevron-up path {
  fill: #9a9a9a;
}

.menu-item.has-children.is-open > .menu-item-content-wrapper > .fa-chevron-up path {
  fill: #4291b2;
}

.menu-item.has-children.is-open > .menu-item-content-wrapper > .fa-chevron-up {
    transform:rotate(90deg);
}

.menu-item-content-wrapper:hover {
    background: rgba(0, 0, 0, .12);
}

.sub-menu > .menu-item > .menu-item-content-wrapper {
    padding: 13px 5px 13px 39px;
    width: calc(100% - 44px);
}

li.menu-item.has-children {
    flex-wrap: wrap;
}


.sub-menu .menu-item-content-wrapper.has-action-left {
    padding-left: 8px;
    width: calc(100% - 13px);
}

.sub-menu .menu-item-content-wrapper.has-action-right {
    padding-right: 14px;
    width: calc(100% - 28px);
}

li.menu-item.has-children > .sub-menu {
    display: none;
}

.menu-item.has-children.is-open > .sub-menu{
  display:block;
}

li.menu-item.is-selected > .menu-item-content-wrapper{
    padding:13px 5px 13px 8px;
    width:calc(100% - 13px);
}

li.menu-item > .menu-item-content-wrapper > svg.left-icon{
    width: 16px;
    margin-right: 15px;   
}

li.menu-item:not(.is-selected) > .menu-item-content-wrapper > svg.left-icon{
    display:none;
}

.simplebar-content-wrapper[style*=scroll]{
    padding-right:9px
}

.sidebar-header[data-tab="sidebar-trigger-maps"] + .sidebar-body .first-level-menu:not(#sidebar-panel-maps) {
    display: none;
}
.sidebar-header[data-tab="sidebar-trigger-analysis"] + .sidebar-body .first-level-menu:not(#sidebar-panel-analysis) {
    display: none;
}
.sidebar-header[data-tab="sidebar-trigger-poi"] + .sidebar-body .first-level-menu:not(#sidebar-panel-poi) {
    display: none;
}


.sidebar-header[data-tab="sidebar-trigger-maps"] + .sidebar-body .first-level-menu#sidebar-panel-maps {
    display: block;
}
.sidebar-header[data-tab="sidebar-trigger-analysis"] + .sidebar-body .first-level-menu#sidebar-panel-analysis {
    display: block;
}
.sidebar-header[data-tab="sidebar-trigger-poi"] + .sidebar-body .first-level-menu#sidebar-panel-poi {
    display: block;
}

.sidebar-body {
    height: 300px;
    overflow: auto;
}

.sidebar-header {
    display: flex;
}
/*****************/

#sample{
    border-radius: 2px;
    border:1px solid gray;
    position: relative;
    z-index: 0;
    width: 550px;
}

.container{
    display:flex;
    justify-content: space-between;
}

