#sidebar {
  cursor: pointer;
  font-family: "Open sans";
  width: 356px;
  user-select: none;
  margin-right: 10px;
  display: flex;
  flex-direction: column;
  border:1px solid rgba(0, 0, 0, .12);
}

.sidebar-body {
  width: 100%;
}

.sidebar-body ul {
  margin: 0;
  padding: 0;
}

.sidebar-body li {
  padding: 0;
  list-style-type: none;
}

.sidebar-body li:hover {
  background: transparent;
}

.sidebar-body li:last-of-type {
  border-bottom: none;
}

.sidebar-body>ul {
  display: none;
}



#sidebar .sidebar-tab {
  cursor: pointer;
  padding: 12px 24px;
  background: #4490B0;
  display: inline-block;
  color: #fff;
  border-right: 1px solid rgba(255, 255, 255, .3);
}

#sidebar .sidebar-tab i {
  margin-right: 10px;
}

#sidebar .sidebar-tab:first-of-type {
  border-radius: 4px 0 0 4px;
}

#sidebar .sidebar-tab:last-of-type {
  border-radius: 0 4px 4px 0;
}

#sidebar .sidebar-tab-radio {
  display: none;
