     * The <li> menu item
     */
    let menuItem;
    let actionButton;

    if(evTarget.classList.contains("menu-item-content-wrapper")){
      menuItem = evTarget.parentElement;
    }
    else if(evTarget.classList.contains("right-icon")){
      actionButton = evTarget;
    }

