              "id":"analysis_1_1",
              "label":"submenu item 1",
              "icons":
              {
                "left":[{
                  "color": "red",
                  "classes": [
                      "fas",
                      "fa-motorcycle"
                  ]
                }],
                "right":[{
                  "id":"analysis_1_action_1",
                  "classes": [
                      "fas",
                      "fa-info-circle"
                  ],
                  "action":"info-circle-analysis_1_action_1",
                  "hasState":true
                }]
              }
            },
            {
              "id":"analysis_1_2",
              "label":"submenu item 2"
            },
            {
              "id":"analysis_1_3",
              "label":"submenu item 3",
            }
          ]
        }
      ],
      "poi":[
        {
          "id":"poi_1",
          "label":"poi menu entry 3"
        }
      ]
    }
  };
  const defaultConfigs = sidebarEntries.defaults;
  //console.log(sidebarEntries);

  const tabIcons = sidebarEntries.tabs;
  // create menu tabs

  prepareSidebarStyleSheet(defaultConfigs);

  sidebarElement.insertAdjacentHTML('beforeend', '<div class="sidebar-header" data-tab="sidebar-trigger-maps"></div>');
  const sidebarHeader = document.querySelector('.sidebar-header');

  Object.keys(sidebarEntries.menus).forEach((key) => {
    if(key != "defaults"){
      sidebarHeader.insertAdjacentHTML('beforeend', `<input class="sidebar-tab-radio" id="sidebar-trigger-${key}" name="sidebar-tabs-group" type="radio" ${key === 'maps' ? 'checked="checked"' : ''}><label class="sidebar-tab" id="sidebar-tab-${key}" for="sidebar-trigger-${key}"><i class="fas fa-${tabIcons[key].icon}"></i>${tabIcons[key].name.toUpperCase() == 'MAPS' ? 'MAPS': tabIcons[key].name}</label>`);
    }
  });

  sidebarElement.insertAdjacentHTML('beforeend', '<div class="sidebar-body"></div>');
  const sidebarBody = document.querySelector('.sidebar-body');

  Object.entries(sidebarEntries.menus).forEach(([key, firstLevelItems]) => {
    if(key != "defaults"){
      sidebarBody.insertAdjacentHTML('beforeend', `<ul class="first-level-menu" id="sidebar-panel-${key}"></ul>`);
      const firstLevelMenu = sidebarBody.querySelector('ul:last-of-type');

      renderMenuItems(firstLevelMenu, firstLevelItems, 1);

      sidebarBody.appendChild(firstLevelMenu);
    }
  });

  ////////:

  sidebarElement.addEventListener("click", function(event){
    //event.stopImmediatePropagation();
    console.log("click event.target = ", event.target);
    let evTarget = event.target;
    let actionStr = "";
    /**
     * The <li> menu item
     */
    let menuItem;
    let actionButton;
    let toggleItemButton;

    if(evTarget.classList.contains("menu-item-content-wrapper")){
      menuItem = evTarget.parentElement;
    }
    else if(evTarget.classList.contains("right-icon")){
      actionButton = evTarget;
    }
    else if(evTarget.classList.contains("toggle-menu-item-btn")){
      toggleItemButton = evTarget;
    }

    console.log("menuItem = ", menuItem);
    console.log("actionButton = ", actionButton);

    if(menuItem){
      if(menuItem.classList.contains("has-children")){
        menuItem.classList.toggle("is-open");
      }
      else{
        menuItem.classList.toggle("is-selected");
        updateMenuSelectionIcon(menuItem,'item');

        actionStr += `<div>Toggling item</div>
        <div>item id: ${menuItem.dataset.id}</div>
        <div>item toggling state: ${menuItem.classList.contains("is-selected")}</div><hr>`

        let closestMenu = menuItem.closest(".menu-item.has-children");

        if(closestMenu){
          actionStr += `<div>parent menu item id ${closestMenu.dataset.id}</div>
        <div>parent menu item toggling state: ${closestMenu.classList.contains("is-selected")}</div>`
        }
      }
    }
    else if(actionButton){
      if(actionButton.classList.contains("has-state")){
        actionButton.classList.toggle("state-on");
      }

      actionStr += `<div>specific action name: ${actionButton.dataset.action}</div>
      <div>action id: ${actionButton.dataset.id}</div>
      <div>action toggling state: ${actionButton.classList.contains("has-state") ? actionButton.classList.contains("state-on") : 'not togglable'}</div>
      `
    }
    else if(toggleItemButton){
      updateMenuSelectionIcon(toggleItemButton, 'itemsbtn')
    }

    document.querySelector("#action-monitor").innerHTML = actionStr;
  });
  ///////////////////////////
  sidebarElement.addEventListener("change", function(event){
    //console.log("event.target = ", event.target);
    let evTarget = event.target;
    console.log("change ev target = ", event.target);
    if(evTarget.classList.contains("sidebar-tab-radio")){
      evTarget.parentElement.dataset.tab = evTarget.id;
    }
  });
  ///////////////////////////
  new SimpleBar(document.querySelector('.sidebar-body'),{ autoHide: defaultConfigs.scrollbarAutohide });

}());

