/**
 * Prepare a specific theming stylesheet for the sidebar using the default configs
 */
function prepareSidebarStyleSheet(defaultConfigs){
    let str = 
    `
    #sidebar[id] .sidebar-tab[class]{
      background:${defaultConfigs.tabBackground};
      color:${defaultConfigs.tabColor};
    }
    #sidebar #sidebar-trigger-maps:checked~#sidebar-tab-maps, 
    #sidebar #sidebar-trigger-analysis:checked~#sidebar-tab-analysis, 
    #sidebar #sidebar-trigger-poi:checked~#sidebar-tab-poi{
      background:${defaultConfigs.tabSelectedBackground};
      color:${defaultConfigs.tabSelectedColor};
    }
    .simplebar-scrollbar::before {
      ${defaultConfigs.scrollbarBackground ? ('background-color:'+defaultConfigs.scrollbarBackground+" !important;"):''}
      opacity: ${defaultConfigs.scrollbarOpacity} !important;
      ${defaultConfigs.scrollbarBackgroundImage ? ('background-image:'+defaultConfigs.scrollbarBackgroundImage+" !important;"):''}
    }
    li.menu-item > .menu-item-content-wrapper > i.left-icon{
      color: ${defaultConfigs.leftIconColor};
    }

    li.menu-item.has-children.is-open > .menu-item-content-wrapper > .fa-chevron-up{
      color: ${defaultConfigs.openChevronIconColor};
    }

    li.menu-item.has-children:not(.is-open) > .menu-item-content-wrapper > .fa-chevron-up{
      color: ${defaultConfigs.closedChevronIconColor};
    }

    li.menu-item > .menu-item-content-wrapper > i.right-icon{
      color: ${defaultConfigs.rightIconColor};
    }

    li.menu-item > .menu-item-content-wrapper > i.right-icon:not(.state-on):hover{
      filter:drop-shadow(0px 0px 1px ${defaultConfigs.rightIconHoverColor});
    }

    li.menu-item > .menu-item-content-wrapper > i.right-icon.has-state.state-on{
      color: ${defaultConfigs.rightIconStateOnColor};
    }
    `

    let sidebarStylesheet = document.createElement("style");
    sidebarStylesheet.id = "sidebar-stylesheet";
    document.head.appendChild(sidebarStylesheet);
    sidebarStylesheet.textContent = str;

}

function updateMenuSelectionIcon(element, operation){
  if(operation == "item"){
    let menu = element.parentElement;

    let children = menu.children;
    let selectedItems = Array.from(children).filter((child)=>{
      return child.classList.contains("is-selected") && !child.classList.contains("disabled");
    });

    if(!menu.classList.contains("first-level-menu")){//not first level menu ?
      let parentelement = menu.closest(".menu-item");

      parentelement.classList.remove("is-semi-selected");
      parentelement.classList.remove("is-selected");

      if(selectedItems.length == children.length){
        parentelement.classList.add("is-selected");
      }
      else if(selectedItems.length >= 1){
        parentelement.classList.add("is-semi-selected");
      }
    } 
  }
  else if(operation == "itemsbtn"){
    let submenu = element.parentElement.nextElementSibling;
    let menu = element.parentElement.parentElement;
    let children = submenu.children;
    let disabledItems = Array.from(children).filter((child)=>{
      return child.classList.contains("disabled");
    });

    if(!menu.classList.contains("disabled")){
      if(menu.classList.contains("is-selected") || menu.classList.contains("is-semi-selected")){ //selected ? unselect everything
        menu.classList.remove("is-semi-selected");
        menu.classList.remove("is-selected");

        Array.from(children).forEach((menuItem)=>{
          if(!menuItem.classList.contains("disabled")){
            menuItem.classList.remove("is-selected");
          }
        });
      }
      else{ //unselected ? select everything
        if(disabledItems.length < children.length && disabledItems.length >= 1){
          menu.classList.add("is-semi-selected");
        }
        else if(!disabledItems.length){
          menu.classList.add("is-selected");
        }
        Array.from(children).forEach((menuItem)=>{
          if(!menuItem.classList.contains("disabled")){
            menuItem.classList.add("is-selected");
          }
        });
      }
    }
  }

}

/**
 * render <li> items of a given menu
 */
function renderMenuItems(menu, itemsArr, depth){

  if(itemsArr.length){
    itemsArr.forEach((itemObj)=>{

      let menuItem = document.createElement("li");
      menuItem.classList.add("menu-item");

      let menuItemContentWrapper = document.createElement("div");
      menuItemContentWrapper.classList.add("menu-item-content-wrapper");

      menuItemContentWrapper.insertAdjacentHTML("beforeend",`<span class="item-label">${itemObj.label}</span>`);

      //console.log("menu = ", menu);
      //console.log("itemObj = ", itemObj);
      //console.log("menuItem = ", menuItem);
      //console.log("_________________________________");

      menuItem.appendChild(menuItemContentWrapper);

      if(itemObj["id"]){
        menuItem.dataset.id = itemObj["id"];
        menuItemContentWrapper.dataset.menuId = itemObj["id"];
      }

      if(depth > 1){//submenu
        if(itemObj["icons"]){
          let leftIcons = itemObj["icons"]["left"];
          let rightIcons = itemObj["icons"]["right"];

          console.log("icons = ", itemObj["icons"]);


          if(leftIcons){
            leftIcons.forEach((leftIconObj)=>{
              let leftIconEl;
              //console.log("leftIconObj = ", leftIconObj);

              if(leftIconObj.tag){
                leftIconEl = document.createElement(leftIconObj.tag);
              }
              else{
                leftIconEl = document.createElement("i");
              }

              if(leftIconObj.classes){
                leftIconObj.classes.forEach((className)=>{
                  leftIconEl.classList.add(className);
                });
              }
              else{
                leftIconEl.classList.add("fas");
                leftIconEl.classList.add("fa-check");
              }
              leftIconEl.classList.add("left-icon");

              if(leftIconObj.color){
                leftIconEl.style.cssText += "color: "+leftIconObj.color;
              }

              if(leftIconObj.id){
                leftIconEl.dataset.id = leftIconObj.id;
              }

              if(leftIconObj.toggledClassesOnClick){
                leftIconEl.dataset.toggledClassesOnClick = JSON.stringify(leftIconObj.toggledClassesOnClick);
              }

              menuItemContentWrapper.insertBefore(leftIconEl,menuItemContentWrapper.firstElementChild);

            });
          }

          if(rightIcons){
            rightIcons.forEach((rightIconObj)=>{
              let rightIconEl;
              //console.log("rightIconObj = ", rightIconObj);

              if(rightIconObj.tag){
                rightIconEl = document.createElement(rightIconObj.tag);
              }
              else{
                rightIconEl = document.createElement("i");
              }

              if(rightIconObj.classes){
                rightIconObj.classes.forEach((className)=>{
                  rightIconEl.classList.add(className);
                });
              }
              else{
                rightIconEl.classList.add("fas");
                rightIconEl.classList.add("fa-check");
              }
              rightIconEl.classList.add("right-icon");

              if(rightIconObj.color){
                rightIconEl.style.cssText += "color: "+rightIconObj.color;
              }

              if(rightIconObj.id){
                rightIconEl.dataset.id = rightIconObj.id;
              }

              if(rightIconObj.action){
                rightIconEl.dataset.action = rightIconObj.action;
              }  

              if(rightIconObj.toggledClassesOnClick){
                rightIconEl.dataset.toggledClassesOnClick = JSON.stringify(rightIconObj.toggledClassesOnClick);
              }

              if(rightIconObj.hasState){
                rightIconEl.classList.add("has-state");
              }

              menuItemContentWrapper.appendChild(rightIconEl);

            });
          }

        }
        else{
          let leftIconEl = document.createElement("i");
          leftIconEl.classList.add("fas");
          leftIconEl.classList.add("fa-check");
          leftIconEl.classList.add("left-icon");
          menuItemContentWrapper.insertBefore(leftIconEl, menuItemContentWrapper.firstElementChild);
        }
      }

      menuItemContentWrapper.insertAdjacentHTML("afterbegin",'<a class="toggle-menu-item-btn"></a>');

      if(itemObj["state"] == "off"){
        menuItem.classList.add("disabled");
        menuItemContentWrapper.insertAdjacentHTML("afterbegin",'<i class="fas fa-lock left-icon"></i>');
      }

      if(itemObj["children"] && itemObj["children"].length){
        menuItemContentWrapper.insertAdjacentHTML("beforeend",'<i class="fas fa-chevron-up"></i>');
        menuItem.classList.add("has-children");

        let subMenu = document.createElement("ol");
        subMenu.classList.add("sub-menu");
        menuItem.appendChild(subMenu);

        renderMenuItems(subMenu, itemObj["children"], depth+1);

      }

      menu.appendChild(menuItem);
    });
  }

}

