          "children":[
            {
              "id":"analysis_1_1",
              "label":"submenu item 1",
              "icons":
              {
                "left":[{
                  "color": "red",
                  "classes": [
                      "fas",
                      "fa-motorcycle"
                  ]
                }],
                "right":[{
                  "id":"analysis_1_action_1",
                  "classes": [
                      "fas",
