#sample{
    border-radius: 2px;
    border:1px solid gray;
    position: relative;
    z-index: 0;
    width:500px;
}

.container{
    display:flex;
    justify-content: space-between;
}

.container .cell{
  display: flex;
  flex-direction: column;
}
